mapper.select:
select

from voucher_day_cut_job
where `VOUCHER_DAY` =#{voucherDay} and TASK_ID = #{taskId}

mapper.update:
update voucher_day_cut_job
`TASK_EXECUTE_STATE`= #{taskExecuteState}, `TASK_EXECUTE_DESC` = #{taskExecuteDesc}, `END_TIME` = now(), `UPDATE_TIME` = now()
where `VOUCHER_DAY` =#{voucherDay} and TASK_ID = #{taskId}
